Operation.Action:

if(OwnerId<=0){		
	Confirm('货主不可为空','OK','OK:{}');
	return true;
}

if(GetRowCount('Grid0',false)<=0){
	Confirm('物料明细不可为空','OK','OK:{}');
	return true;
}

//步骤明细检查，并构成物流步骤描述
var checkmaterialID = false;
var index = 0;
var s = "";
while(index<GetRowCount('Grid1')){
	var i = index + 1;
	if(GetCellValue('Grid1', index, 'StepType') == 'wms'){
		checkmaterialID = true;
		//仓储中心、仓储业务类型不可为空
		if(GetCellValue('Grid1', index, 'WarehouseCenterId')<=0){
			Confirm('第'+i+'条步骤明细的仓储中心不能为空', 'OK','OK:{}');
			return true;
		}	

		if(GetCellValue('Grid1', index, 'WMSOrderType')==""){
			Confirm('第'+i+'条步骤明细的仓储业务类型不能为空', 'OK','OK:{}');
			return true;
		}			
		s = s + GetCellCaption('Grid1', index, 'WMSOrderType') + '(' + GetCellCaption('Grid1', index, 'WarehouseCenterId') +') - ';
	}

	if(GetCellValue('Grid1', index, 'StepType') == 'tms'){
		//调度组织、运输业务类型不可为空
		if(GetCellValue('Grid1', index, 'DispatchOrgId')<=0){
			Confirm('第'+i+'条步骤明细的调度组织不能为空', 'OK','OK:{}');
			return true;
		}	

		if(GetCellValue('Grid1', index, 'TMSOrderType')==""){
			Confirm('第'+i+'条步骤明细的运输业务类型不能为空', 'OK','OK:{}');
			return true;
		}

		if(GetCellValue('Grid1', index, 'TMSOrderType') == "LTL" || GetCellValue('Grid1', index, 'TMSOrderType') == "MT"){
			if(GetCellValue('Grid1', index, 'SendOrgId')<=0){
				Confirm('第'+i+'条步骤明细的发站不能为空', 'OK','OK:{}');
				return true;
			}

			if(GetCellValue('Grid1', index, 'DestOrgId')<=0){
				Confirm('第'+i+'条步骤明细的到站不能为空', 'OK','OK:{}');
				return true;
			}
		}

		s = s + GetCellCaption('Grid1', index, 'TMSOrderType') + '(' + GetCellCaption('Grid1', index, 'DispatchOrgId') +') - ';						
	}
	index = index + 1;
}
//如果有仓储步骤则要检查物料id不为空
if(checkmaterialID){
	index = 0;
	while(index<GetRowCount('Grid0')-1){
		var i = index + 1;
		if(GetCellValue('Grid0', index, 'MaterialId')<=0){
			Confirm('第'+i+'条物料明细的物料不能为空', 'OK','OK:{}');
			return true;
		}	
		if(GetCellValue('Grid0', index, 'MaterialUnit')<=0){
			Confirm('第'+i+'条物料明细的单位不能为空', 'OK','OK:{}');
			return true;
		}
		index = index + 1;
	}
}
if(Length(s) > 0){
	s = Left(s, Length(s)-3);
}
StepDescription = s;

SaveData();
UpdateView();

DataBinding.ValueChanging:

Confirm('改变货主后将清空收货方和物料明细，是否继续？','YES_NO',
	{YES:{
		CommitValue('OwnerId');
		ClearAllRows('Grid0');
		RecvPartyId = -1;
		RecvPartyName = "";
		RecvAddr = "";
		RecvProvinceId = -1;
		RecvCityId = -1;
		RecvDistrictId = -1; 

		SendPartyId = -1;
		SendPartyName = "";
		SendAddr = "";
		SendProvinceId = -1;
		SendCityId = -1;
		SendDistrictId = -1; 
		},
	NO:{RollbackValue('OwnerId');}});

DataBinding.ValueChanged:

if(StepType == 'wms'){
	DispatchOrgId = -1;
	TMSOrderType = "";
	DestOrgId = -1;
	SendOrgId = -1;
	IsQuick = 0;
}
if(StepType == 'tms'){
	WarehouseCenterId = -1;
	WMSOrderType = "";
	NeedExchange = 0;
	PickAllowLack = 0;
}
