Operation.Action:
                if(GetValue('OwnerId') <= 0 || GetValue('NoticeNo') == ''){
	Confirm(LocaleString("OwnerNull", "key137"), 'OK',{OK:{}});
	return true;
}
ShowModal('V_QuoteQuantView',
	{WarehouseCenterId_para:{WarehouseCenterId},
	OwnerId_para:{OwnerId},
	pFormKey:{GetFormKey()},
	RefOrderNo_para:{NoticeNo},
	IsKittingArea_para:{1},
	IsAllSelect_para:{1}
	});

                var flag = false;
var flag1 = false;
var rs = DBNamedQuery("SelectMaSta", TxTypeCode);
if(GetRowCount('Grid1', false)<=0 || GetRowCount('Grid3', false)<=0){ 
	Confirm(LocaleString("OwnerNull", "key148"), 'OK','OK:{}');
	return true;
}
var index = 0;
var index1 = 0;
loop 'Grid1' (true){
        index = index + 1;
        var mtlStatusId = GetCellValue('Grid1', -1, 'SrcMaterialStatusId');
	rs.beforefirst();
	while(rs.next()){
		if(mtlStatusId > 0 && rs.MaterialStatusId == mtlStatusId){
			flag=true;
			break;
		}
                flag = false;
	}
	if(flag == false){
		  Confirm(LocaleParaFormat("CommonDef", "key55", index) + GetDictValue('LRP_MaterialStatus', mtlStatusId, 'Name'), 'OK', 'OK:{}');
                  break;	
	}   
}
loop 'Grid3' (true){
        index1 = index1 + 1;
        var mtlStatusId2 = GetCellValue('Grid3', -1, 'DestMaterialStatusId');
	rs.beforefirst();
	while(rs.next()){
		if(mtlStatusId2 > 0 && rs.MaterialStatusId == mtlStatusId2){
			flag1=true;
			break;
		}
                flag1 = false;
	}
	if(flag1 == false){
		  Confirm(LocaleParaFormat("CommonDef", "key55", index1) + GetDictValue('LRP_MaterialStatus', mtlStatusId2, 'Name'), 'OK', 'OK:{}');
                  break;	
	}   
}
if(flag == true && flag1 == true){
	SaveData(); 
	UpdateView();
}

                Confirm(LocaleString("CommonDef", "key3"),'YES_NO',{YES:{
Status = 900;
if(ToString(FinishDateTime) == ''){
        SetValue('FinishDateTime', ServerDate());
    }
loop "Grid1" (true){			
  WMTx_Status = 90;
}
loop "Grid3" (true){			
  WMTx_Status_P = 90;
}
SaveData();
UpdateView();
},NO:{}});

                Confirm(LocaleString("CommonDef", "key4"),'YES_NO',{YES:{
Status=100;
if(ToString(FinishDateTime) != ''){
      SetValue('FinishDateTime', '');
}
loop "Grid1" (true){			
    WMTx_Status = 10;
}
loop "Grid3" (true){			
    WMTx_Status_P = 10;
}
SaveData();
UpdateView();
},NO:{}});

Grid.RowClick:
                            if(2 == GetDictValue('LRP_Material',MaterialId,'SerialControlMethod') || 1 == GetDictValue('LRP_Material',MaterialId,'SerialControlMethod')){
	SetNewEmptyRow("Grid2",true);
}else{									
	SetNewEmptyRow("Grid2",false);
}
var s =  "物料："+GetDictValue('LRP_Material',MaterialId,'Name')+"，序列号个数："+GetRowCount("Grid2",false);									
SetValue("Description_1",s);

                            if(2 == GetDictValue('LRP_Material',MaterialId_P,'SerialControlMethod') || 1 == GetDictValue('LRP_Material',MaterialId,'SerialControlMethod')){
	SetNewEmptyRow("Grid4",true);
}else{									
	SetNewEmptyRow("Grid4",false);
}
var s =  "物料："+GetDictValue('LRP_Material',MaterialId_P,'Name')+"，序列号个数："+GetRowCount("Grid4",false);									
SetValue("Description_2",s);
